mapper.select:
select sum(c.CollNum) as SumCollNum ,Ztab .* from ( select a.BatNo,a.BatName,b.EmpId,b.EmpNo,b.EmpName,c.* from Tbb_Batch a, Tbb_Employee b ,Tbb_Collar c where a.BatId=c.BatId and b.EmpId = c.EmpId and b.EmpStatus1 !=-1 and c.Status !=-1 and a.Status=1
and b.EmpNo like '%'+rtrim(ltrim(#{EmpNo,jdbcType=VARCHAR}))+'%'
and b.EmpName like '%'+rtrim(ltrim(#{EmpName,jdbcType=VARCHAR}))+'%'
and c.CollNo like '%'+rtrim(ltrim(#{CollNo,jdbcType=VARCHAR}))+'%'
and c.Status =#{Status,jdbcType=INTEGER}
and c.CollTime >= #{BeginDate,jdbcType=DATE}
and c.CollTime <= #{EndDate,jdbcType=DATE}
) as Ztab ,Tbb_Collar c where Ztab .CollId=c.CollId

mapper.delete:
update Tbb_Collar
set Status = -1
where CollId = #{CollId,jdbcType=INTEGER}
