mapper.select:
select count(EmpId)  FROM Tbb_Employee
where EmpName = #{EmpName,jdbcType=VARCHAR}
and EmpPassword =#{EmpPassword,jdbcType=VARCHAR}
and EmpStatus1 !=-1
and EmpStatus2 =1

SELECT *
FROM Tbb_Employee
where EmpName = #{EmpName,jdbcType=VARCHAR}
and EmpPassword =#{EmpPassword,jdbcType=VARCHAR}

mapper.update:
update Tbb_Employee
set EmpStatus1=-1
where EmpId = #{EmpId,jdbcType=INTEGER}
